INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'lamellar (stone)'), 'description', 'Lamellar is a type of armor in which small plates of various types of materials are strung together in parallel rows using fine cord. Lamellar plates can be constructed from lacquered leather, horn, or even stone, though suits of iron and steel are the most common. Lamellar armor can be crafted into various shapes, including partial pieces such as breastplates, greaves, or even entire coats. The properties of specific suits and pieces of lamellar armor are determined by their material.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'lamellar (stone)'), 'source', 'Ultimate Equipment pg. 9');
INSERT INTO irori_objects(name, type) VALUES('double-plated', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'double-plated'), 'cost', '+1,000 gp');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'double-plated'), 'weight', '+20 lbs.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'double-plated'), 'description', 'Plating adds protection to a suit of armor’s wearer at the cost of mobility. Double-plated armor’s armor bonus is increased by 1, and its maximum Dexterity bonus is reduced by 2. DrawbackDouble-plated armor’s armor category (light, medium, or heavy) is considered to be one heavier. Heavy armor that has the modification added to it is too restrictive to use without the Armor Adept (double-plated) feat (see page 11).');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'double-plated'), 'source', 'Adventurer''s Armory 2 pg. 7');
