INSERT INTO irori_objects(name, type) VALUES('razored', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razored'), 'cost', '+1,350 gp');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razored'), 'weight', '+10 lbs.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razored'), 'description', 'With every plate sharpened to a cutting edge, razored armor punishes those who get too close. Armor with the razored modification counts as having masterwork armor spikes; if the armor already had armor spikes or masterwork armor spikes, decrease the cost of the razored modification by the cost of the existing armor spikes. Whenever an opponent hits you with a melee touch attack or succeeds at a combat maneuver check to grapple, she must succeed at a Reflex saving throw (DC = 10 + your base attack bonus) or take an amount of piercing damage equal to 1d6 plus the armor’s enhancement bonus. DrawbackRazored armor reduces your speed by 5 feet. In addition, razored armor takes twice as long to don or remove.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razored'), 'source', 'Adventurer''s Armory 2 pg. 7');
