INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'nimble'), 'description', 'Nimble armor is designed for flexibility and ease of use, although this compromises its protective qualities. Nimble armor’s maximum Dexterity bonus is increased by 2, and its armor check penalty is reduced by 1 (minimum 0). DrawbackNimble armor’s armor bonus is reduced by 1.');
