INSERT INTO irori_objects(name, type) VALUES('deflecting', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'deflecting'), 'cost', '+1,000 gp');
