INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'vitalguard'), 'description', 'This well-designed armor reinforces your defenses, adding layers of padding or mail to your most vulnerable locations. Vitalguard armor’s bonus to AC is increased by 2 for the purpose of confirming critical hits against you. DrawbackVitalguard armor reduces your speed by 5 feet.');
