INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'rapier'), 'description', 'You can use the Weapon Finesse feat to apply your Dexterity modifier instead of your Strength modifier to attack rolls with a rapier sized for you, even though it isn’t a light weapon. You can’t wield a rapier in two hands in order to apply 1-1/2 times your Strength modifier to its damage.');
