INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'ripsaw glaive'), 'description', 'The blade of this glaive is serrated and mounted on an axle. A heavy cord is wrapped around the axle; when pulled (a move action), the blade spins rapidly for a number of rounds equal to your Strength bonus. While the blade is spinning, it deals +2 damage; otherwise, treat this weapon as a glaive. Re-wrapping the cord around the spinning mechanism is a full-round action that provokes an attack of opportunity.');
