INSERT INTO irori_objects(name, type) VALUES('pellets (handful)', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pellets (handful)'), 'cost', '1 gp');
