INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'flying blade'), 'description', 'A flying blade is a large, crescent-shaped blade anchored to a 10-foot chain. You fight with a flying blade by spinning it about your head or by snapping it toward an opponent. Because the flying blade is a clumsy weapon, you receive a -2 penalty on attack rolls with it, but when you make attacks of opportunity provoked by movement, you receive a +2 bonus on attack rolls instead of the penalty.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'flying blade'), 'source', 'Ranged Tactics Toolbox pg. 33');
INSERT INTO irori_objects(name, type) VALUES('razor-sharp', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razor-sharp'), 'cost', '+1,000 gp');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razor-sharp'), 'weight', 'none');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razor-sharp'), 'description', 'The weapon’s edge is honed to incredible sharpness. The weapon’s edge dulls after a number of daily uses, but the bonus can be reset by spending 15 minutes whetting the blade after a night’s rest. Only piercing or slashing weapons can have the razor-sharp modification. Projectile weapons can’t be razorsharp, but their ammunition can. A razor-sharp weapon gains a +1 bonus on weapon damage rolls for a number of attacks each day equal to 3 + your base attack bonus.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'razor-sharp'), 'source', 'Adventurer''s Armory 2 pg. 10');
