INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'halfling spongestones (10)'), 'special', 'none');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'halfling spongestones (10)'), 'description', 'These bullets are actually made from a porous stone or heavy wood that absorbs liquids and shatters on impact. Spongestones are often used to deliver poison or burning oil. A flask of oil completely saturates 10 spongestones. When ignited (a free action if fire is available), a burning, oil-soaked spongestone deals +1d2 fire damage and can ignite combustible materials (such as a thatched roof ). Allowing a spongestone to burn in a sling’s cup for more than 1d3 rounds gives the sling the broken condition.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'halfling spongestones (10)'), 'source', 'Halflings of Golarion pg. 23');
INSERT INTO irori_objects(name, type) VALUES('pitted bullet', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pitted bullet'), 'cost', '5 gp');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pitted bullet'), 'weight', 'none');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pitted bullet'), 'description', 'This ammunition is pitted with a pattern of small pocks into which specially formulated poison compounds can be applied. A poison compound is a derivative of a standard toxin that is alchemically reduced to a solid form. These can be made from any standard injury or contact poison with a successful Craft (poison) check equal to the poison’s DC + 4. The cost of purchasing an already prepared poison compound for the purpose of treating pitted bullets is equal to the poison’s base cost + 20 gp. Once crafted, the compound can be pasted into the ammunition’s pitted design and allowed to harden. Upon completion, the bullet can be fired from an appropriate firearm, releasing the poison compound into its target upon impact, but the poison’s DC is reduced by 2. A pitted bullet cannot be used with an alchemical cartridge. The listed cost does not include the cost of poison.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pitted bullet'), 'source', 'Ultimate Equipment pg. 41');
INSERT INTO irori_objects(name, type) VALUES('heavy crossbow', 'item');
