INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'light slavers crossbow'), 'description', 'This crossbow shoots ammunition called bolas bolts instead of crossbow bolts. It comes in both light and heavy varieties (but not double, hand, or repeating crossbow varieties) with the same reload action as a conventional crossbow of its type.');
