INSERT INTO irori_objects(name, type) VALUES('dual-balanced', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'dual-balanced'), 'cost', '+2,000 gp');
