INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'kama'), 'description', 'Similar to a sickle—and in some regions still used to reap grain—a kama is a short, curved blade attached to a simple handle, usually made of wood. It is sometimes also referred to as a kai, and is frequently used in pairs by martial artists.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'kama'), 'source', 'Ultimate Equipment pg. 20');
INSERT INTO irori_objects(name, type) VALUES('pellets (30 handfuls)', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pellets (30 handfuls)'), 'cost', '30 gp');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pellets (30 handfuls)'), 'weight', '1/2 lb.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pellets (30 handfuls)'), 'description', 'A handful of pellets, along with 1 dose of black powder, is commonly used as ammunition for one-handed and twohanded firearms with the scatter weapon quality, though rocks or other small bits of hard material can be used in the pellets’ place. Using anything other than pellets or alchemical cartridges when firing off a cone attack with a scatter weapon increases the weapon’s misfire range by 1 (though this increase can be removed by switching back to standard ammunition). Thirty handfuls of pellets weigh half a pound.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'pellets (30 handfuls)'), 'source', 'Ultimate Combat pg. 1');
INSERT INTO irori_objects(name, type) VALUES('m1914 grenade (concussion)', 'item');
