INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'falchion'), 'source', 'Ultimate Equipment pg. 18');
INSERT INTO irori_objects(name, type) VALUES('firearm bullet, silver', 'item');
