INSERT INTO irori_objects(name, type) VALUES('lawrence 1917 flamethrower', 'item');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'lawrence 1917 flamethrower'), 'cost', '800 gp');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'lawrence 1917 flamethrower'), 'weight', '20 lbs.');
