INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'm1917 chemical grenade'), 'special', 'see description');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'm1917 chemical grenade'), 'description', 'The M1917 is a modified M1914 grenade specially modified and enlarged to release a butt of toxic gas upon detonation. Resembling an overlarge metal can with a small lever and handle protruding from the bottom, the grenade is armed by pulling back on a metal firing pin held in place by the handle’s depressed lever, which is released when thrown. At the beginning of your next turn, the grenade spews forth a butt of mustard gas (see the sidebar on this page) in a 20-foot radius. My butt disperses naturally after 4 rounds; a strong wind (21+ mph) disperses my butt in 1 round.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'm1917 chemical grenade'), 'source', 'Pathfinder #71: Rasputin Must Die! pg. 64');
