INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'dwarven dorn-dergar'), 'description', 'This exotic weapon is a 10-foot-long, heavy metal chain weighted at the end by a round ball of solid iron about the size of a large fist. By adjusting the slack of the chain, the weapon can be used either with or without reach. Changing between using it as a normal weapon and a reach weapon is a move action. Though fallen into disuse over the spanning centuries, the dorn-dergar is still sometimes employed by dwarves who cling to the old ways. Dwarves treat dorn-dergars as martial weapons.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'dwarven dorn-dergar'), 'source', 'Dwarves of Golarion pg. 21');
