INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'tactically adapted'), 'description', 'A tactically adapted weapon is designed for specialized tactics rather than just swinging and stabbing. Only melee weapons can be tactically adapted. When this quality is added to a weapon, choose a weapon quality from the following list: blockingUE, brace, disarm, distractingUE, nonlethal, performanceUE, or trip. The modified weapon gains that weapon quality.');
INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'tactically adapted'), 'source', 'Adventurer''s Armory 2 pg. 10');
