INSERT INTO irori_stats(object, stat, value) VALUES( (SELECT id FROM irori_objects WHERE name = 'wahaika'), 'description', 'This short and broad Stone Age club is made of hardened wood or bone. It has a notch on one side that is used for catching weapons. If you are proficient with the weapon, you can use the notch in the wahaika to disarm your foes. Otherwise, treat this weapon as a club. Feats and abilities that affect clubs apply to the wahaika.');
